/*
r = relative Luftfeuchte
T = Temperatur in °C
TK = Temperatur in Kelvin (TK = T + 273.15)
TD = Taupunkttemperatur in °C
DD = Dampfdruck in hPa
SDD = Sättigungsdampfdruck in hPa
*/

var R = 8314.3
var MW = 18.016

var a, b;


function SDD(T){
    if(T>=0){
        a = 7.5;
        b = 237.3; 
    } else {
        a = 7.6;
        b = 240.7; 
    }

    return 6.1078 * Math.pow(10,(a*T)/(b+T))
}

function DD(r, T){
    return r/100 * SDD(T)
}

function TD(r, T){
    var v = Math.log10(DD(r, T)/6.1078)
    return b*v/(a-v)
}

